import{_ as a,g as e,ad as o,f as n}from"./chunks/framework.C88Rnq0V.js";const u=JSON.parse('{"title":"组件设计原则","description":"","frontmatter":{},"headers":[],"relativePath":"components.md","filePath":"components.md"}'),i={name:"components.md"};function l(r,t,s,c,d,h){return n(),e("div",null,t[0]||(t[0]=[o('<h1 id="组件设计原则" tabindex="-1">组件设计原则 <a class="header-anchor" href="#组件设计原则" aria-label="Permalink to &quot;组件设计原则&quot;">​</a></h1><h2 id="一致性-consistency" tabindex="-1">一致性 Consistency <a class="header-anchor" href="#一致性-consistency" aria-label="Permalink to &quot;一致性 Consistency&quot;">​</a></h2><ul><li><strong>与现实生活一致</strong>：与现实生活的流程、逻辑保持一致，遵循用户习惯的语言和概念。</li><li><strong>在界面中一致</strong>：所有的元素和结构需保持一致，比如设计样式、图标和文本、元素的位置等。</li></ul><h2 id="反馈-feedback" tabindex="-1">反馈 Feedback <a class="header-anchor" href="#反馈-feedback" aria-label="Permalink to &quot;反馈 Feedback&quot;">​</a></h2><ul><li><strong>控制反馈</strong>：通过界面样式和交互动效让用户可以清晰地感知自己的操作。</li><li><strong>页面反馈</strong>：操作后，通过页面元素的变化清晰地展现当前状态。</li></ul><h2 id="效率-efficiency" tabindex="-1">效率 Efficiency <a class="header-anchor" href="#效率-efficiency" aria-label="Permalink to &quot;效率 Efficiency&quot;">​</a></h2><ul><li><strong>简化流程</strong>：设计简洁直观的操作流程。</li><li><strong>清晰明确</strong>：语言表达清晰且表意明确，让用户快速理解进而作出决策。</li><li><strong>帮助用户识别</strong>：界面简单直白，让用户快速识别而非回忆，减少用户记忆负担。</li></ul><h2 id="可控-controllability" tabindex="-1">可控 Controllability <a class="header-anchor" href="#可控-controllability" aria-label="Permalink to &quot;可控 Controllability&quot;">​</a></h2><ul><li><strong>用户决策</strong>：根据场景可给予用户操作建议或安全提示，但不能代替用户进行决策。</li><li><strong>结果可控</strong>：用户可以自由地进行操作，包括撤销、回退和终止当前操作等。</li></ul>',9)]))}const g=a(i,[["render",l]]);export{u as __pageData,g as default};
